# -*- coding: utf-8 -*-"""Created on Thu Mar 24 11:02:14 2016__version__ = '0.1.2'@author:Zhao Cheng"""from collections import Iteratordef cut_seq(items, slices):    """    To slice sequence by var slices    :param items: Input list,tuple or Generator.    :param slices: slices number    :return: Generator which yield slices tuple.    """    if isinstance(items, (list, tuple)):        if len(items) < slices:            yield tuple(items,)        else:            for index in range(0, len(items), slices):                yield tuple(items[index:index + slices])    elif isinstance(items, Iterator):        itemlist = list()        for item in items:            itemlist.append(item)            if len(itemlist) == slices:                yield tuple(itemlist)                itemlist = list()        if len(itemlist) > 0:            yield tuple(itemlist)    else:        raise TypeError('<base.tools.cut_seq> Must input list tuple or Generator')def make_urls(codes, slices, prefix, suffix, separator):    """    To make the url for ntes api    :param codes:Input codes must be a Iterator, list or tuple    :param slices: code slices for once inputting url    :param prefix: code's url prefix    :param suffix: code's url suffix    :param separator: code's separator    :return: generator for getting data url    """    if isinstance(codes, (Iterator, list, tuple)):        for code_tuple in cut_seq(codes, slices=slices):            yield prefix + separator.join(tuple(map(str, code_tuple))) + suffix    else:        raise TypeError('<base.url.make_urls>Must input a generator,list or tuple')def override(x, y):    """    To use for updating dict y's key to dict x's key include sub dict's key.    :param x: must be dict. To be updating.    :param y: must be dict. update y's key to x.    :return: return dict x which is updated by y.    """    for k, v in y.items():        if not isinstance(v, dict):            x[k] = v        else:            if k in x.keys():                override(x[k], v)            else:                x[k] = v    return xdef merge(x, y):    """    To use for merge dict y's key and dict x's key include sub dict's key.    :param x: must be dict. To be merging.    :param y: must be dict. TO be merging.    :return: return new dict  which was merged x.key and y.key.    """    return override(x.copy(), y)